class 'GUISpectator' (GUIScript)function GUISpectator:Initialize()    if self.guiUnitFrames == nil then        self.guiUnitFrames = GetGUIManager():CreateGUIScriptSingle("GUIInsight_PlayerFrames")    end    if self.guiTopBar == nil then        self.guiTopBar = GetGUIManager():CreateGUIScriptSingle("GUIInsight_TopBar")    end       if self.guiLocation == nil then        self.guiLocation = GetGUIManager():CreateGUIScriptSingle("GUIInsight_Location")    end        if self.guiAlertQueue == nil then        self.guiAlertQueue = GetGUIManager():CreateGUIScriptSingle("GUIInsight_AlertQueue")    end
    if self.guiGraphs == nil then        self.guiGraphs = GetGUIManager():CreateGUIScriptSingle("GUIInsight_Graphs")    end    end    function GUISpectator:Uninitialize()    if self.guiUnitFrames then            GetGUIManager():DestroyGUIScriptSingle("GUIInsight_PlayerFrames")        self.guiUnitFrames = nil            end        if self.guiTopBar then            GetGUIManager():DestroyGUIScriptSingle("GUIInsight_TopBar")        self.guiTopBar = nil            end        if self.guiLocation then            GetGUIManager():DestroyGUIScriptSingle("GUIInsight_Location")        self.guiLocation = nil            end        if self.guiAlertQueue then            GetGUIManager():DestroyGUIScriptSingle("GUIInsight_AlertQueue")        self.guiAlertQueue = nil            end
    if self.guiGraphs then        GetGUIManager():DestroyGUIScriptSingle("GUIInsight_Graphs")        self.guiGraphs = nil    end
    endfunction GUISpectator:SetIsVisible(visible)    self.guiUnitFrames:SetIsVisible(visible)    self.guiTopBar:SetIsVisible(visible)    self.guiLocation:SetIsVisible(visible)    self.guiAlertQueue:SetIsVisible(visible)    end